#include<iostream>

using namespace std;

int main() {
    int n,i,a1,a2,a3,a4,a5;
    cin>>n;
    for(i=0; i<n; i++) {
        cin>>a1>>a2>>a3>>a4>>a5;
        cout<<a1<<' '<<a2<<' '<<a3<<' '<<a4<<' '<<a5<<' ';
        cout << a1+a2+a3+a4+a5 << endl;
    }
    return 0;
}

#include<cstdio>

using namespace std;

int main() {
    int flag;
    char c;
    c = getchar();
    while (c != EOF) {
        if (c <= 'm')
            printf("%c\n", c+1);
        else
            printf("%c\n", c-1);
        c = getchar();
        c = getchar();
    }
    return 0;
}

#include<cstdio>

using namespace std;

int main() {
    double x;
    while (scanf("%lf", &x) == 1) {
        if (x == 2.0)
            printf("%.4lf\n", x);
        else
            printf("%.4f\n", 1.0 / (x - 2.0));
    }
    return 0;
}

#include <cstdio>

using namespace std;

int main() {
    double p, w, s, d, f;
    while (scanf("%lf %lf %lf", &p, &w, &s) == 3) {
        if (s < 250.0) {
            d = 0;
        }
        else if (s < 500.0) {
            d = 0.02;
        }
        else if (s < 1000) {
            d = 0.08;
        }
        else if (s < 2000) {
            d = 0.1;
        }
        else {
            d = 0.15;
        }
        f = p * w * s * (1.0 - d);
        printf("%.2lf\n", f);
    }
    return 0;
}
