#include <iostream>
using namespace std;
int main(){
	int map[21][21];
	int m,n;
	int r=0;
	while(cin>>m>>n){
		if(r!=0) cout<<endl;
		r++;
		for(int i=0;i<m;i++){
			for(int j=0;j<n;j++){
				cin>>map[i][j];
				if(map[i][j]!=0){
					cout<<i+1<<" "<<j+1<<" "<<map[i][j]<<endl;
				}
			}
		}
	}
	return 0;
}

#include <iostream>   //
using namespace std;   //

bool check(int i)   //
{
     while(i>0)   //
     {
          if(i%10==1 || i%10==0)   //
          {
              i/=10;   //
          }
          else
              return false;   //
     }
     return true;   //
}

int main()   //
{
    int n;   //
    int i;   //
    while(cin>>n)   //
    {
        for(i=1;;i++)   //
        {
            if(check(i*n))   //
            {
                cout<<i*n<<endl;   //
                break;   //
            }
        }
    }
    return 0;   //
}

#include <iostream>
using namespace std;

typedef struct
{
    char hua;
    int num;
    int v;
}card;

card c[4][13],ct;

int main()
{
    int i,j,nn,n;
    char t;
    int r=0;
    cin>>nn;
    while(nn--)
    {
	if(r!=0) cout<<endl;
	r++;
        for(i=0;i<4;i++)
        {
            for(j=0;j<13;j++)
            {
                cin>>c[i][j].hua;
                switch(c[i][j].hua)
                {
                    case 'S':c[i][j].v=4;break;
                    case 'H':c[i][j].v=3;break;
                    case 'D':c[i][j].v=2;break;
                    case 'C':c[i][j].v=1;break;
                }
                cin>>t;
                if(t>='2' && t<='9')
                    c[i][j].num=t-'0';
                else
                {
                    switch(t)
                    {
                        case 'A':c[i][j].num=14;break;
                        case 'K':c[i][j].num=13;break;
                        case 'Q':c[i][j].num=12;break;
                        case 'J':c[i][j].num=11;break;
                        case 'T':c[i][j].num=10;break;
                    }
                }
            }
        }
        for(n=0;n<4;n++)
        {
            for(i=0;i<13;i++)
            {
                for(j=0;j<13-i-1;j++)
                {
                    if(c[n][j].num<c[n][j+1].num)
                    {
                        ct = c[n][j];
                        c[n][j] = c[n][j+1];
                        c[n][j+1] = ct;
                    }
                    else if(c[n][j].num==c[n][j+1].num)
                    {
                        if(c[n][j].v<c[n][j+1].v)
                        {
                            ct = c[n][j];
                            c[n][j] = c[n][j+1];
                            c[n][j+1] = ct;
                        }
                    }
                }
            }
        }
        for(i=0;i<4;i++)
        {
            for(j=0;j<12;j++)
            {
                cout<<c[i][j].hua;
                if(c[i][j].num>=10)
                    switch(c[i][j].num)
                    {
                        case 14:cout<<"A";break;
                        case 13:cout<<"K";break;
                        case 12:cout<<"Q";break;
                        case 11:cout<<"J";break;
                        case 10:cout<<"T";break;
                    }
                else
                    cout<<c[i][j].num;
                cout<<" ";
            }
            cout<<c[i][j].hua;
            if(c[i][j].num>=10)
            switch(c[i][j].num)
            {
                case 14:cout<<"A";break;
                case 13:cout<<"K";break;
                case 12:cout<<"Q";break;
                case 11:cout<<"J";break;
                case 10:cout<<"T";break;
            }
            else
                cout<<c[i][j].num<<endl;
        }
    }


    return 0;
}

#include <iostream>
using namespace std;
#include <string>


int main()
{
    string str,ts;
    int len;
    int i;
    int w,l;
    while(cin>>ts)
    {
        str="";
        str += ts;
        len = ts.length();
        if(ts[len-1]!='E')
        {
            while(cin>>ts)
            {
                str += ts;
                len = ts.length();
                if(ts[len-1]=='E')
                    break;
            }
        }
        w=l=0;
        for(i=0;i<str.length()-1;i++)
        {
            if(str[i]=='W')
                w++;
            else
                l++;
            if((w>=11 && w-l>=2) || (l>=11 && l-w>=2))
            {
                cout<<w<<":"<<l<<endl;
                w=l=0;
            }
        }
        if(w+l!=0)
            cout<<w<<":"<<l<<endl;
        cout<<endl;

        w=l=0;
        for(i=0;i<str.length()-1;i++)
        {
            if(str[i]=='W')
                w++;
            else
                l++;
            if((w>=21 && w-l>=2) || (l>=21 && l-w>=2))
            {
                cout<<w<<":"<<l<<endl;
                w=l=0;
            }
        }
        if(w+l!=0)
            cout<<w<<":"<<l<<endl;
    }
    return 0;
}
